/*Use Wrapper class methods to print MaxValue, MinValue and Size for various 
primitive types
*/
package Assignment4;

public class EightProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       System.out.println("Data types\t\t MAX_VALUE  \t\t MIN_VALUE \t\t SIZE");
       System.out.println("Bytes"+"\t\t\t"+Byte.MAX_VALUE+"\t\t\t"+Byte.MIN_VALUE+"\t\t\t"+Byte.SIZE);
       System.out.println("Short"+"\t\t\t"+Short.MAX_VALUE+"\t\t\t"+Short.MIN_VALUE+"\t\t\t"+Short.SIZE);
       System.out.println("int"+"\t\t\t"+Integer.MAX_VALUE+"\t\t"+Integer.MIN_VALUE+"\t\t"+Integer.SIZE);
       System.out.println("Float"+"\t\t\t"+Float.MAX_VALUE+"\t\t"+Float.MIN_VALUE+"\t\t\t"+Float.SIZE);
       System.out.println("Double"+"\t\t\t"+Double.MAX_VALUE+"\t"+Double.MIN_VALUE+"\t\t"+Double.SIZE);
       System.out.println("Long"+"\t\t\t"+Long.MAX_VALUE+"\t"+Long.MIN_VALUE+"\t"+Long.SIZE);
      
	}

}
